landwirt = 0;
kirchenbeitrag = 0;

if (children_count === 1) {
  children_money = 22;
} else if (children_count === 2) {
  children_money = 44;
} else if (children_count >= 3) {
  children_money = 44 + (children_count - 2) * 36;
}

