} else if (eingaben.alleinstehend_angekreuzt && children_money === 0) {
  console.warn("Alleinverdiener-/Alleinerzieherabsetzbetrag nur mit Kindern");
}

absatz_summe += children_money;
kirchenbeitrag += brutto * 0.011 - absatz_std;

if (eigenbesitz <= lw_w_1 && eigenbesitz > 0) {
  landwirt += eigenbesitz * lw_p_1;
} else if (eigenbesitz <= lw_w_2 && eigenbesitz > 0) {
  landwirt += (eigenbesitz - lw_w_1) * lw_p_2 + lw_w_1 * lw_p_1;
} else if (eigenbesitz <= lw_w_3 && eigenbesitz > 0) {
  landwirt += (eigenbesitz - lw_w_2) * lw_p_3 + lw_w_2 * lw_p_2;
} else if (eigenbesitz > lw_w_3 && eigenbesitz > 0) {
  landwirt += (eigenbesitz - lw_w_3) * lw_p_4 + (lw_w_2 * lw_p_2 + lw_w_3 * lw_p_3 + lw_w_1 * lw_p_1);
