} else if (gepachtet <= lw_w_2 && gepachtet > 0) {
  landwirt += ((gepachtet - lw_w_1) * lw_p_2 + lw_w_1 * lw_p_1) * 0.75;
} else if (gepachtet <= lw_w_3 && gepachtet > 0) {
  landwirt += ((gepachtet - lw_w_2) * lw_p_3 + lw_w_2 * lw_p_2) * 0.75;
} else if (gepachtet > lw_w_3 && gepachtet > 0) {
