  landwirt += verpachtet * lw_p_1 * 0.25;
} else if (verpachtet <= lw_w_2 && verpachtet > 0) {
  landwirt += ((verpachtet - lw_w_1) * lw_p_2 + lw_w_1 * lw_p_1) * 0.25;
