  landwirt += ((verpachtet - lw_w_2) * lw_p_3 + lw_w_2 * lw_p_2) * 0.25;
} else if (verpachtet > lw_w_3 && verpachtet > 0) {
  landwirt += ((verpachtet - lw_w_3) * lw_p_4 + (lw_w_2 * lw_p_2 + lw_w_3 * lw_p_3 + lw_w_1 * lw_p_1)) * 0.25;
}

if (brutto === 0 && landwirt > 0) {
  kirchenbeitrag += landwirt + absatz_std;
  kirchenbeitrag -= absatz_summe;
  if (kirchenbeitrag < mindestbeitrag) {
    kirchenbeitrag = mindestbeitrag;
  }
} else {
  kirchenbeitrag -= absatz_summe;
  if (kirchenbeitrag < mindestbeitrag) {
    kirchenbeitrag = mindestbeitrag;
  }
  kirchenbeitrag += landwirt;
}

if (brutto - gss_w_1 < kirchenbeitrag && brutto - gss_w_1 > 0) {
  steuerausgleich += (brutto - gss_w_1) * gss_p_1 * gss_p_1;
} else if (brutto <= gss_w_2) {
  steuerausgleich += kirchenbeitrag * gss_p_1;
} else if (brutto - gss_w_2 < kirchenbeitrag && brutto - gss_w_2 > 0) {
  steuerausgleich += (brutto - gss_w_2) * gss_p_2 * gss_p_2 + (kirchenbeitrag - (brutto - gss_w_2) * gss_p_2) * gss_p_1;
} else if (brutto <= gss_w_3) {
  steuerausgleich += kirchenbeitrag * gss_p_2;
} else if (brutto - gss_w_3 < kirchenbeitrag && brutto - gss_w_3 > 0) {
  steuerausgleich += (brutto - gss_w_3) * gss_p_3 * gss_p_3 + (kirchenbeitrag - (brutto - gss_w_3) * gss_p_3) * gss_p_2;
} else if (brutto <= gss_w_4) {
  steuerausgleich += kirchenbeitrag * gss_p_3;
