  steuerausgleich += (brutto - gss_w_4) * gss_p_4 * gss_p_4 + (kirchenbeitrag - (brutto - gss_w_4) * gss_p_4) * gss_p_3;
} else if (brutto <= gss_w_5) {
  steuerausgleich += kirchenbeitrag * gss_p_4;
} else if (brutto - gss_w_5 < kirchenbeitrag && brutto - gss_w_5 > 0) {
  steuerausgleich += (brutto - gss_w_5) * gss_p_5 * gss_p_5 + (kirchenbeitrag - (brutto - gss_w_5) * gss_p_5) * gss_p_4;
} else if (brutto <= gss_w_6) {
  steuerausgleich += kirchenbeitrag * gss_p_5;
} else if (brutto - gss_w_6 < kirchenbeitrag && brutto - gss_w_6 > 0) {
  steuerausgleich += (brutto - gss_w_6) * gss_p_6 * gss_p_6 + (kirchenbeitrag - (brutto - gss_w_6) * gss_p_6) * gss_p_5;
} else if (brutto > gss_w_6) {
  steuerausgleich += kirchenbeitrag - kirchenbeitrag * gss_p_6;
}

if (steuerausgleich > max_steuerausgleich) {
  steuerausgleich = max_steuerausgleich;
}
if (steuerausgleich < 0 || brutto === 0) {
  steuerausgleich = 0;
}

