// Get the user's message
const userMessage = $input.item.json.chatInput || $input.item.json.message || "";

// Initial greeting with buttons
if (!userMessage || userMessage === "start" || userMessage === "hello") {
  return {
    output: `Welcome! 👋 How can I help you today?

\`\`\`buttons
{
  "title": "Choose a department:",
  "options": [
    {"label": "💼 Sales", "value": "SALES"},
    {"label": "🛠️ Technical Support", "value": "SUPPORT"},
    {"label": "💳 Billing", "value": "BILLING"},
    {"label": "📞 Contact Us", "value": "CONTACT"}
  ]
}
\`\`\`
`
  };
}

// Sales flow
if (userMessage === "SALES") {
  return {
    output: `Great! You selected Sales. 💼

Do you already have a customer number?

\`\`\`buttons
{
  "options": [
    {"label": "✅ Yes, I have one", "value": "HAS_CUSTOMER_NUMBER"},
    {"label": "❌ No, I'm new", "value": "NO_CUSTOMER_NUMBER"}
  ]
}
\`\`\`
`
  };
}

if (userMessage === "HAS_CUSTOMER_NUMBER") {
  return {
    output: "Perfect! Please enter your customer number and we'll look you up in our system."
  };
}

if (userMessage === "NO_CUSTOMER_NUMBER") {
  return {
    output: `Welcome to our sales team! 🎉

What are you interested in?

\`\`\`buttons
{
  "options": [
    {"label": "Products", "value": "PRODUCTS"},
    {"label": "Pricing", "value": "PRICING"},
    {"label": "Demo", "value": "DEMO"}
  ]
}
\`\`\`
`
  };
}

// Support flow
if (userMessage === "SUPPORT") {
  return {
    output: `Technical Support here! 🛠️

What kind of issue are you experiencing?

\`\`\`buttons
{
  "title": "Select issue type:",
  "options": [
    {"label": "🌐 Website Issue", "value": "WEB_ISSUE"},
    {"label": "🔌 API Problem", "value": "API_ISSUE"},
    {"label": "📱 Mobile App", "value": "MOBILE_ISSUE"},
    {"label": "❓ Other", "value": "OTHER_ISSUE"}
  ]
}
\`\`\`
`
  };
}

if (userMessage === "WEB_ISSUE" || userMessage === "API_ISSUE" || userMessage === "MOBILE_ISSUE") {
  return {
    output: `I understand you're having issues. Let me connect you with a specialist.

Would you like to:

\`\`\`buttons
{
  "options": [
    {"label": "📧 Email Support", "value": "EMAIL_SUPPORT"},
    {"label": "💬 Live Chat", "value": "LIVE_CHAT"},
    {"label": "📚 Check Documentation", "value": "DOCS", "url": "https://docs.n8n.io"}
  ]
}
\`\`\`
`
  };
}

// Billing flow
if (userMessage === "BILLING") {
  return {
    output: `Billing Department 💳

How can we help with your billing?

\`\`\`buttons
{
  "options": [
    {"label": "View Invoice", "value": "VIEW_INVOICE"},
    {"label": "Payment Issue", "value": "PAYMENT_ISSUE"},
    {"label": "Change Plan", "value": "CHANGE_PLAN"},
    {"label": "Cancel Subscription", "value": "CANCEL"}
  ]
}
\`\`\`
`
  };
}

// Contact flow
if (userMessage === "CONTACT") {
  return {
    output: `Contact Information 📞

Choose your preferred contact method:

\`\`\`buttons
{
  "options": [
    {"label": "📧 Email", "value": "CONTACT_EMAIL", "url": "mailto:support@example.com"},
    {"label": "📞 Phone", "value": "CONTACT_PHONE"},
    {"label": "🌐 Website", "value": "CONTACT_WEB", "url": "https://example.com/contact"}
  ]
}
\`\`\`
`
  };
}

// Default response for any other input
return {
  output: `I received: "${userMessage}"

Would you like to start over?

\`\`\`buttons
{
  "options": [
    {"label": "🔄 Start Over", "value": "start"},
    {"label": "❌ End Chat", "value": "END"}
  ]
}
\`\`\`
`
};
